    /**
     * 检查邮箱格式
     * @param in 待验证信息
     * @return
     */
    public static boolean isEmail(String in) {
        return isMatched(EMAIL, in);
    }

    /**
     * 检查是否是QQ
     * @param num
     * @return
     */
    public static boolean isQQ(String qq) {
        return isMatched(QQ, qq);
    }

    /**
     * 正则验证公共方法
     * @param regex 正则
     * @param in 待验证信息
     * @return
     */
    private static boolean isMatched(String regex, String in) {
        Pattern pattern = Pattern.compile(regex);
        Matcher matcher = pattern.matcher(in);
        boolean isMatched = matcher.matches();
        return isMatched;
    }
