//comment提交验证
function submit_comment(){
	var c=$('#comment').find('textarea');
	var content=c.val();
	
	if(content==c.attr('title')) content='';
	if(content==''){
		$('#comment').find('.message').html('您还没有写评论的内容');
		return false;
	}else if(content.length>140){
		$('#comment').find('.message').html('最多只能写140字哦~');
		return false;
	}else if(content.length<2){
		$('#comment').find('.message').html('至少要写两个字吧');
		return false;
	}else{
		$('#comment-submit').attr("disabled",true).val('提交中...').css('background','#bcbcbd');
		$.ajax({
			url : addCommentUrl+'?callback=?',
			dataType : 'jsonp',
			data : {
				'content': content,
				'obj_info' : $('#object-comment').attr('obj_info')
			},
			success : function(ret){
				if(ret.code>0){
					if(ret.code==2){
						$.tips({text:ret.data.taskMessage});
					}
					$('#comment').find('.message').html('');
					$('#comment-list').find('.nocomment').remove();
					$('#comment-submit').val('完成');
					c.val(c.attr('title'));
					$('#comment-list').prepend(ret.data.html);
					setTimeout(function(){
						$('#comment-submit').attr("disabled",false).val('评论').css('background','#04ce9b');
					},10000);
					$('#comment').find('textarea').css('color','#c9c9c9');
				}else{
					$('#comment').find('.message').html(ret.message);
					$('#comment-submit').attr("disabled",false).val('评论').css('background','#04ce9b');
				}
			}
		})
	}
	return false;
}

