//页面登录
function login(){
	loginQ();
	//登录的提交操作
	$.validator.setDefaults({       
		submitHandler: function() {
			$('#form_login').find(".btn").attr("disabled", true).val('登录中...').css('background','#bcbcbd');
			$.ajax({
				type : 'POST',
				url : loginUrl,
				dataType : 'json',
				data : $('#form_login').serialize(),
				success : function(response){
					if(response.code > 0) {
						if(response.data.redirectUrl==''){
							window.location.reload();
						}else{
							window.location.href=response.data.redirectUrl;
						}
					} else {
						$('li').find('span.valid').removeClass('valid').addClass('error');
						$('.username').parent('li').find('span').html(response.message);
						$('#form_login').find(".btn").attr("disabled", false).val('登录').css('background','#04ce9b');
					}
				}
			})
		}
	}),
	//登录的文本验证
	$('#form_login').validate({
		onkeyup : false,
		rules:{
			email : {
				required : true
				//email : true,
				//remote : checkUserEmailExistUrl
			},
			password : {
				required : true,
				minlength : 5
			}
		},
		messages : {
			email : {
				required : '邮箱不能为空'
				//email : '请输入正确的邮箱地址',
				//remote : '该邮箱未注册过'
			},
			password : {
				required : '密码不能为空',
				minlength : '不能小于5位'
			}
		},
		errorElement: "span",
		errorPlacement: function (error, element) { 
      		if (element.is(':radio') || element.is(':checkbox')) {
          		var eid = element.attr('name');
          		error.appendTo(element.parent());
      		} else {
          		error.appendTo(element.closest("li"));
     		}
		},
		focusInvalid: true,
		success:function(e)
		{
			e.html('&nbsp;').addClass('valid');
		}
	})
}

